mapper.insert:
insert into board_manage( title, content, writer)
VALUES(
      #{title}, 
      #{content}, 
      #{writer}
)

mapper.select:
SELECT * 
FROM customer_manage
WHERE id = #{id} AND pwd = #{pwd}

mapper.update:
UPDATE customer_manage SET

		name = #{name}
		, pwd = #{pwd}
		, email = #{email}
		, custRank = #{custRank},
WHERE 
		id = #{id}
		)
